// importfunctionalities
import React from 'react';
import logo from './logo.svg';
import './App.css';
import {
  Connection,
  PublicKey,
  clusterApiUrl,
  Keypair,
  LAMPORTS_PER_SOL,
  SystemProgram,
  Transaction,
  sendAndConfirmTransaction,
} from "@solana/web3.js";
import {useEffect , useState } from "react";

// create types
type DisplayEncoding = "utf8" | "hex";

type PhantomEvent = "disconnect" | "connect" | "accountChanged";
type PhantomRequestMethod =
  | "connect"
  | "disconnect"
  | "signTransaction"
  | "signAllTransactions"
  | "signMessage";

interface ConnectOpts {
  onlyIfTrusted: boolean;
}

// create a provider interface (hint: think of this as an object) to store the Phantom Provider
interface PhantomProvider {
  publicKey: PublicKey;
  isConnected: boolean | null;
  signTransaction: (transaction: Transaction) => Promise<Transaction>;
  signAllTransactions: (transactions: Transaction[]) => Promise<Transaction[]>;
  signMessage: (
    message: Uint8Array | string,
    display?: DisplayEncoding
  ) => Promise<any>;
  connect: (opts?: Partial<ConnectOpts>) => Promise<{ publicKey: PublicKey }>;
  disconnect: () => Promise<void>;
  on: (event: PhantomEvent, handler: (args: any) => void) => void;
  request: (method: PhantomRequestMethod, params: any) => Promise<unknown>;
}

/**
 * @description gets Phantom provider, if it exists
 */
 const getProvider = (): PhantomProvider | undefined => {
  if ("solana" in window) {
    // @ts-ignore
    const provider = window.solana as any;
    if (provider.isPhantom) return provider as PhantomProvider;
  }
};
const DEMO_FROM_SECRET_KEY = new Uint8Array(
  [
      160,  20, 189, 212, 129, 188, 171, 124,  20, 179,  80,
       27, 166,  17, 179, 198, 234,  36, 113,  87,   0,  46,
      186, 250, 152, 137, 244,  15,  86, 127,  77,  97, 170,
       44,  57, 126, 115, 253,  11,  60,  90,  36, 135, 177,
      185, 231,  46, 155,  62, 164, 128, 225, 101,  79,  69,
      101, 154,  24,  58, 214, 219, 238, 149,  86
    ]            
);

const transferSol = async() => {
  const connection = new Connection(clusterApiUrl("devnet"), "confirmed");

  // Get Keypair from Secret Key
  var from = Keypair.fromSecretKey(DEMO_FROM_SECRET_KEY);

  // Other things to try: 
  // 1) Form array from userSecretKey
  // const from = Keypair.fromSecretKey(Uint8Array.from(userSecretKey));
  // 2) Make a new Keypair (starts with 0 SOL)
  // const from = Keypair.generate();

  // Generate another Keypair (account we'll be sending to)
  const to = Keypair.generate();

  // Aidrop 2 SOL to Sender wallet
  console.log("Airdopping some SOL to Sender wallet!");
  const fromAirDropSignature = await connection.requestAirdrop(
      new PublicKey(from.publicKey),
      2 * LAMPORTS_PER_SOL
  );

  // Latest blockhash (unique identifer of the block) of the cluster
  let latestBlockHash = await connection.getLatestBlockhash();

  // Confirm transaction using the last valid block height (refers to its time)
  // to check for transaction expiration
  await connection.confirmTransaction({
      blockhash: latestBlockHash.blockhash,
      lastValidBlockHeight: latestBlockHash.lastValidBlockHeight,
      signature: fromAirDropSignature
  });

  console.log("Airdrop completed for the Sender account");

  // Send money from "from" wallet and into "to" wallet
  var transaction = new Transaction().add(
      SystemProgram.transfer({
          fromPubkey: from.publicKey,
          toPubkey: to.publicKey,
          lamports: LAMPORTS_PER_SOL / 100
      })
  );

  // Sign transaction
  var signature = await sendAndConfirmTransaction(
      connection,
      transaction,
      [from]
  );
  console.log('Signature is ', signature);
}

transferSol();

function App() {
  // create state variable for the secretKey
  const [secretKey, setKeypair] = useState<Uint8Array>(PublicKey.default.toBytes());
  const [solanaAcc, setAccount] = useState<boolean | undefined>(undefined);
  // create state variable for the provider
  const [provider, setProvider] = useState<PhantomProvider | undefined>(
    undefined
  );

	// create state variable for the wallet key
  const [walletKey, setWalletKey] = useState<PhantomProvider | undefined>(
  undefined
);

  // this is the function that runs whenever the component updates (e.g. render, refresh)
  useEffect(() => {
	  const provider = getProvider();

		// if the phantom provider exists, set this as the provider
	  if (provider) setProvider(provider);
	  else setProvider(undefined);
  }, []);

  /**
   * @description prompts user to connect wallet if it exists.
	 * This function is called when the connect wallet button is clicked
   */
  const connectWallet = async () => {
    // @ts-ignore
    const { solana } = window;

		// checks if phantom wallet exists
    if (solana) {
      try {
				// connects wallet and returns response which includes the wallet public key
        const response = await solana.connect();
        console.log('wallet account ', response.publicKey.toString());
				// update walletKey to be the public key
        setWalletKey(response.publicKey.toString());
      } catch (err) {
      // { code: 4001, message: 'User rejected the request.' }
      }
    }
  };

  const createSolanaAccount = async () => {
    try {
      if(solanaAcc === true) return;
      console.log("trying to connect to devnet");
      const connection = new Connection(clusterApiUrl("devnet"), "confirmed");
      const newPair = Keypair.generate();
      console.log("first wallet pub key is ", newPair.publicKey.toString());
      // Airdrops 2 SOL to the newly created Keypair
      const airDropSignature = await connection.requestAirdrop(
        newPair.publicKey,
          2 * LAMPORTS_PER_SOL
      );
      console.log("##1");
      // Latest blockhash (unique identifer of the block) of the cluster
      let latestBlockHash = await connection.getLatestBlockhash();
      console.log("##2");
      // Confirm transaction using the last valid block height (refers to its time)
      // to check for transaction expiration
      await connection.confirmTransaction({
          blockhash: latestBlockHash.blockhash,
          lastValidBlockHeight: latestBlockHash.lastValidBlockHeight,
          signature: airDropSignature
      });
      console.log("Airdrop completed");
      setKeypair(newPair.secretKey);
      console.log('Signature is ', airDropSignature);
      setAccount(true);
      const balance = await connection.getBalance(newPair.publicKey);
      console.log("balance is ", balance / LAMPORTS_PER_SOL, " SOL");
    } catch (err) {
      // { code: 4001, message: 'User rejected the request.' }
    }
  };

  const transferToWallet = async () => {
    console.log("phantom wallet key is ", walletKey);
    try {
      const connection = new Connection(clusterApiUrl("devnet"), "confirmed");
      console.log("after connection");
      console.log("first wallet pub key is ", Keypair.fromSecretKey(secretKey).publicKey.toString());
      if(walletKey == undefined) throw new Error('Phantom wallet undefined!');
      const toPair = Keypair.generate();
        // Send money from "from" wallet and into "to" wallet
        var transaction = new Transaction().add(
          SystemProgram.transfer({
              fromPubkey: Keypair.fromSecretKey(secretKey).publicKey,
              toPubkey: toPair.publicKey,
              lamports: LAMPORTS_PER_SOL / 100
          })
      );
      console.log("sign transaction");
      // Sign transaction
      var signature = await sendAndConfirmTransaction(
          connection,
          transaction,
          [Keypair.fromSecretKey(secretKey)]
      );
      console.log('Signature is ', signature);
      const balance = await connection.getBalance(toPair.publicKey);
      console.log("balance is ", balance / LAMPORTS_PER_SOL, " SOL");
    } catch (err) {
      // { code: 4001, message: 'User rejected the request.' }
    }
  };

	// HTML code for the app
  return (
    <div className="App">
      <header className="App-header">
        <h2>Project for Module 2</h2>
      </header>
      {!solanaAcc && (
          <button
            style={{
              fontSize: "16px",
              padding: "15px",
              fontWeight: "bold",
              borderRadius: "5px",
            }}
            onClick={createSolanaAccount}
          >
            Create a new Solana account
          </button>
        )}
      {solanaAcc && provider && !walletKey && (
          <button
            style={{
              fontSize: "16px",
              padding: "15px",
              fontWeight: "bold",
              borderRadius: "5px",
            }}
            onClick={connectWallet}
          >
            Connect Wallet
          </button>
        )}
        {provider && walletKey && ( 
          <button
          style={{
            fontSize: "16px",
            padding: "15px",
            fontWeight: "bold",
            borderRadius: "5px",
          }}
          onClick={transferToWallet}
        >
          Transfer to new wallet
        </button>
                )}

        {!provider && (
          <p>
            No provider found. Install{" "}
            <a href="https://phantom.app/">Phantom Browser extension</a>
          </p>
        )}
    </div>
  );
}

export default App;
